import { useState } from "react";

function MyCounter(){
    const [counterVal, updateCount] = useState(0);
    
    function increment(){
        updateCount(counterVal += 1);
    }
    function decrement(){
        updateCount(counterVal -= 1);
    }

    return(
        <div>
            <button onClick={decrement}>-</button>
            {counterVal}
            <button onClick={increment}>+</button>
        </div>
    )

}

export default MyCounter
